import { StatementItem } from '../common-types';
import { ApplicationState } from '../reducers';

export const getItemsDataSelector = (state: ApplicationState): StatementItem[] => state.items.data;

export const isItemsDataLoadedSelector = (state: ApplicationState): boolean => state.items.status === 'RECUEST';
