import StatementTypes from '../actions/types/statement';
import { StatementItem } from '../common-types';
import { getStatements } from '../actions/item-actions';

export type ItemReducer = {
  status: string;
  data: StatementItem[];
  error: string;
};

function filter(data: StatementItem[], id: string) {
  return data.filter(el => {
    return el.id !== id;
  });
}

const initialState = {
  status: 'INITIAL',
  data: [],
  error: '',
};

export default function itemReduser(state: ItemReducer = initialState, action: getStatements) {
  switch (action.type) {
    case StatementTypes.RequestStatements:
      return {
        ...state,
        status: 'RECUEST'
      };
    case StatementTypes.SuccessStatements:
      return {
        ...state,
        status: 'SUCCESS',
        data: action.payload
      };
    case StatementTypes.FaledStatements:
      return {
        ...state,
        status: 'FALED',
        data: null
      }
    // case StatementTypes.DeleteItem:
    //   return {
    //     ...state,
    //     data: filter(state.data, action.payload.id),
    //   };
    // case StatementTypes.ChangeItem:
    //   return {
    //     state,
    //     data: action.payload,
    //   };
    // case StatementTypes.AddStatements:
    //   return {
    //     ...state,
    //     data: [...state.data, action.payload],
    //   };
    default:
      return state;
  }
}
